function btnPreview_onclick() {
    FillTableToReport();
    Report.PrintPreview(true);
    window.opener=null;
    window.open('','_self');
    window.close();
}

function btnPrint_onclick() {
    FillTableToReport();
    Report.Print(false);
    window.opener=null;
    window.open('','_self');
    window.close();
}

function btnExportXls_onclick() {
    FillTableToReport();
    //gretXLS = 1,
    Report.ExportDirect(1, "", true,  true);
}
